mapper.select:
SELECT `personnel_addressbook`.*,`personnel_staff`.* FROM `personnel_addressbook`
INNER JOIN `personnel_staff` ON `personnel_staff`.id = `personnel_addressbook`.staffId
WHERE `personnel_staff`.`id`=#{id} OR `personnel_staff`.`staffName`=#{staffname}

SELECT `personnel_dimission`.*,`personnel_staff`.* FROM `personnel_staff`
INNER JOIN `personnel_dimission` ON `personnel_staff`.`id`=`personnel_dimission`.`staffId`
WHERE `personnel_staff`.`id`=#{id} OR `personnel_staff`.`staffName`=#{staffname}
