SELECT `personnel_staff`.*,`personnel_department`.*,`personnel_post`.* FROM `personnel_staff` INNER JOIN `personnel_department` ON `personnel_staff`.`did`=`personnel_department`.`id` INNER JOIN `personnel_post` ON `personnel_staff`.`departmentId`=`personnel_post`.`postId` where 1=1 AND `personnel_staff`.`default2`!=2
and (
`personnel_department`.id = #{item}
)

SELECT `personnel_staff`.*,`personnel_department`.*,`personnel_post`.* FROM `personnel_staff`
INNER JOIN `personnel_department` ON `personnel_staff`.`did`=`personnel_department`.`id`
INNER JOIN `personnel_post` ON `personnel_staff`.`departmentId`=`personnel_post`.`postId`
where 1=1 AND `personnel_staff`.`default2`!=2

  and (


      `personnel_department`.id = #{item}



  )

/*员工编号*/
  and `personnel_staff`.id = #{personnelStaff.id}

/*员工姓名*/
  AND `personnel_staff`.staffname like CONCAT("%",#{personnelStaff.staffname},"%")

/*性别*/
  and `personnel_staff`.sex like CONCAT("%",#{personnelStaff.sex},"%")

/*部门id*/
  AND `personnel_staff`.did = #{personnelStaff.did}

/*员工账户*/
  and `personnel_staff`.staffaccount like CONCAT("%",#{personnelStaff.staffaccount},"%")

/*员工密码*/
  AND `personnel_staff`.password like CONCAT("%",#{personnelStaff.password},"%")

/*员工岗位id*/
  and `personnel_staff`.departmentid like CONCAT("%",#{personnelStaff.departmentid},"%")

/*身体状况*/
  AND `personnel_staff`.physicalcondition like CONCAT("%",#{personnelStaff.physicalcondition},"%")

/*身高*/
  and `personnel_staff`.height like CONCAT("%",#{personnelStaff.height},"%")

/*籍贯*/
  AND `personnel_staff`.nativeplace like CONCAT("%",#{personnelStaff.nativeplace},"%")

/*民族*/
  and `personnel_staff`.nation like CONCAT("%",#{personnelStaff.nation},"%")

/*婚姻状态*/
  AND `personnel_staff`.maritalstatus like CONCAT("%",#{personnelStaff.maritalstatus},"%")

/*学历*/
  and `personnel_staff`.educationbackground like CONCAT("%",#{personnelStaff.educationbackground},"%")

/*毕业学校*/
  AND `personnel_staff`.school like CONCAT("%",#{personnelStaff.school},"%")

/*专业*/
  and `personnel_staff`.major like CONCAT("%",#{personnelStaff.major},"%")

/*专业资格*/
  AND `personnel_staff`.professionalqualification like CONCAT("%",#{personnelStaff.professionalqualification},"%")

/*属性*/
  and `personnel_staff`.property like CONCAT("%",#{personnelStaff.property},"%")

/*学位*/
  AND `personnel_staff`.degree like CONCAT("%",#{personnelStaff.degree},"%")

/*编制*/
  and `personnel_staff`.authorizedstrength like CONCAT("%",#{personnelStaff.authorizedstrength},"%")

/*身份证号*/
  AND `personnel_staff`.idcardno like CONCAT("%",#{personnelStaff.idcardno},"%")

/*户口地址*/
  and `personnel_staff`.residence like CONCAT("%",#{personnelStaff.residence},"%")

/*现居地址*/
  AND `personnel_staff`.presentaddress like CONCAT("%",#{personnelStaff.presentaddress},"%")

/*联系电话*/
  AND `personnel_staff`.contactnumber like CONCAT("%",#{personnelStaff.contactnumber},"%")

/*手机*/
  and `personnel_staff`.phone = like CONCAT("%",#{personnelStaff.phone},"%")

/*邮箱*/
  AND `personnel_staff`.email like CONCAT("%",#{personnelStaff.email},"%")

/*验证码*/
  and `personnel_staff`.authcode like CONCAT("%",#{personnelStaff.authcode},"%")

/*验证码过期时间*/
  AND `personnel_staff`.pastcodedate like CONCAT("%",#{personnelStaff.pastcodedate},"%")

/*开户银行*/
  AND `personnel_staff`.depositbank like CONCAT("%",#{personnelStaff.depositbank},"%")

/*银行账户*/
  AND `personnel_staff`.banaccount like CONCAT("%",#{personnelStaff.banaccount},"%")

/*紧急联系人*/
  and `personnel_staff`.emergencycontact like CONCAT("%",#{personnelStaff.emergencycontact},"%")

/*紧急联系人电话*/
  AND `personnel_staff`.emergencyphone like CONCAT("%",#{personnelStaff.emergencyphone},"%")

/*入职日期*/
  and `personnel_staff`.dateonboard = like CONCAT("%",#{personnelStaff.dateonboard},"%")

/*试用到期时间*/
  AND `personnel_staff`.thetrialdue like CONCAT("%",#{personnelStaff.thetrialdue},"%")

/*出生日期*/
  AND `personnel_staff`.birthdaydate like CONCAT("%",#{personnelStaff.birthdaydate},"%")

/*合同开始时间*/
  and `personnel_staff`.contractstart like CONCAT("%",#{personnelStaff.contractstart},"%")

/*合同到期时间*/
  AND `personnel_staff`.agreementends like CONCAT("%",#{personnelStaff.agreementends},"%")

/*卡自编号*/
  and `personnel_staff`.cardnumber like CONCAT("%",#{personnelStaff.cardnumber},"%")

/*卡内部号*/
  AND `personnel_staff`.internalcardno like CONCAT("%",#{personnelStaff.internalcardno},"%")

/*推荐人*/
  AND `personnel_staff`.referrer like CONCAT("%",#{personnelStaff.referrer},"%")

/*整单折扣权*/
  and `personnel_staff`.wholeorderdiscountright like CONCAT("%",#{personnelStaff.wholeorderdiscountright},"%")

/*工时折扣权*/
  AND `personnel_staff`.timediscountright like CONCAT("%",#{personnelStaff.timediscountright},"%")

/*商品折扣权*/
  and `personnel_staff`.righttodiscount like CONCAT("%",#{personnelStaff.righttodiscount},"%")

/*减免*/
  AND `personnel_staff`.rightofrelief like CONCAT("%",#{personnelStaff.rightofrelief},"%")
/*工作简历*/
AND `personnel_staff`.jobresume like CONCAT("%",#{personnelStaff.jobresume},"%")

/*教育经历*/
  and `personnel_staff`.educationexperience like CONCAT("%",#{personnelStaff.educationexperience},"%")

/*家庭成员*/
  AND `personnel_staff`.memberoffamily like CONCAT("%",#{personnelStaff.memberoffamily},"%")

/*奖惩记录*/
  and `personnel_staff`.disciplinaryrecords like CONCAT("%",#{personnelStaff.disciplinaryrecords},"%")

/*聘用意见*/
  AND `personnel_staff`.employmentadvice like CONCAT("%",#{personnelStaff.employmentadvice},"%")

/*员工相片*/
  AND `personnel_staff`.picture like CONCAT("%",#{personnelStaff.picture},"%")

SELECT `personnel_addressbook`.*,`personnel_staff`.*,`personnel_department`.* FROM `personnel_addressbook`
INNER JOIN `personnel_staff` ON `personnel_staff`.id = `personnel_addressbook`.staffId
INNER JOIN `personnel_department` ON `personnel_addressbook`.`departmentId`=`personnel_department`.`id`
where 1=1
/*工号*/
  and `personnel_staff`.id = #{personnelStaff.id}

/*姓名*/
  AND `personnel_staff`.staffname like CONCAT("%",#{personnelStaff.staffname},"%")

SELECT `personnel_dimission`.*,`personnel_staff`.* FROM `personnel_staff`
INNER JOIN `personnel_dimission` ON `personnel_staff`.`id`=`personnel_dimission`.`staffId`
WHERE `personnel_staff`.`id`=#{id} OR `personnel_staff`.`staffName`=#{staffname}
